import React, { useState, useEffect } from 'react';
import { Text, FlatList, View, Image, TextInput} from 'react-native';


const Exam3cStart = () => {
    const [data,setData] = useState([]);
    const [loading,setLoading] = useState(true);

    const getMeals = async () => {
        try {
          const url = "https://www.themealdb.com/api/json/v1/1/filter.php?i=chicken_breast"
          const response = await fetch(url);
          const json = await response.json();
          setData(json.meals); 
        } catch (error) {
          console.error(error);
        } finally {
            setLoading(false);
        }
      };

    useEffect(() => {getMeals()}, [])
    /*
    return(
        <View>
            <Text>API Demo</Text>
            <Text>{JSON.stringify(data,null,5)}</Text>

        </View>
    ); */
    return(
      <><Text>API Demo</Text><TextInput
        style={{ fontSize: 10 }}
        placeholder="Date/Time"
        onChangeText={text => {
          setDateTime(text);
        } }
        value={dateTime} /></>
    );
}

export default Exam3cStart;
