import React, { useState, useEffect } from 'react';
import { Text, FlatList, View, Image, TextInput} from 'react-native';


const Exam3cStart = () => {
    const [data,setData] = useState([]);
    const [loading,setLoading] = useState(true);
    const [input,setInput] = useState("");

    const getMeals = async () => {
        try {
          const url = "https://www.themealdb.com/api/json/v1/1/filter.php?i=chicken_breast"
          const response = await fetch(url);
          const json = await response.json();
          setData(json.meals); 
        } catch (error) {
          console.error(error);
        } finally {
            setLoading(false);
        }
      };

    useEffect(() => {getMeals()}, [])
    /*
    return(
        <View>
            <Text>API Demo</Text>
            <Text>{JSON.stringify(data,null,5)}</Text>

        </View>
    ); */
    return(
      <View style={{ 
        flexDirection: "column",
        flex: 1
    }}>
        <><Text
          style = {{
            fontSize: 50,
            fontWeight: 500,
            margin: 30
          }}
        >
          API Demo
        </Text>
        <TextInput
          style={{ 
            fontSize: 20,
            margin: 10
           }}
          placeholder="Set your ingredient"
          onChangeText={text => {
            setInput(text);
          } }
          value={input} />
        </>
      </View>
    );
}

export default Exam3cStart;
